	const cookieRegExp = new RegExp(
		`${CookieValueRegularExpressionParts[0]}${encodeURIComponent(cookieName)}${CookieValueRegularExpressionParts[1]}`
	)
	return document.cookie.replace(cookieRegExp, '$1')
}

