import EventHandler from './EventHandler.js'

/**
The abstract class for DataModel and DataCollection
It holds the event mixin and the generic function of fetching data from a remote service
*/
const DataObject = class extends EventHandler {
	/**
	@param {Object} [options={}]
	*/
	constructor(options = {}) {
		super()
		this.options = options
		this._new = true // True until the first fetch returns, regardless of http status
		this.cleanedUp = false
	}
	cleanup() {
		if (this.cleanedUp) return
		this.cleanedUp = true
		super.cleanup()
	}
	/** @type {boolean} true until a fetch (even a failed fetch) returns */
	get isNew() {
		return this._new
	}
	/** @type {string} the URL (relative or full) as a string for the endpoint used by this.fetch */
	get url() {
		throw new Error('Extending classes must implement url()')
	}

	/** Clear out old data and set it to data, should trigger a 'reset' event */
	reset(data = {}) {
		throw new Error('Extending classes must implement reset')
	}
	parse(data) {
		// Extending classes can override this to parse the data received via a fetch
		return data
	}
	equals(obj) {
		// Extending classes can override this to allow less strict equality
		return this === obj
	}
	onFirstReset(func) {
		// If already reset, immediately call func, otherwise wait until the first reset and then call func
		if (this._new) {
			this.addListener(
				() => {
					func(this)
				},
				'reset',
				true
			)
		} else {
			func(this)
		}
	}
	get fetchOptions() {
		// Extending classes can override this to add headers, methods, etc to the fetch call
		return {
			credentials: 'same-origin',
		}
	}
	fetch() {
		// Ask the server for data for this model or collection
		return new Promise(
			function (resolve, reject) {
				this.trigger('fetching', this)
				this._innerFetch(this.url, this.fetchOptions)
					.then((response) => {
						if (response.status != 200) {
							throw 'Fetch failed with status ' + response.status
						}
						return response.json()
					})
					.then((data) => {
						data = this.parse(data)
						this._new = false
						this.reset(data)
						this.trigger('fetched', this, data, null)
						resolve(this)
					})
					.catch((err) => {
						this._new = false
						this.trigger('fetched', this, null, err)
						reject(err)
					})
			}.bind(this)
		)
	}
	/**
	Use this to override the use of window.fetch
	For example, MockService overrides this to intercept fetch calls and return its own responses for matched endpoints
	*/
	_innerFetch(...params) {
		return fetch(...params)
	}

	/**
	Fetch each DataObject and then wait for them all to return
	Note: this resolves when the fetches complete, regardless of whether they succeed or fail.
	*/
	static fetchAll(...dataObjects) {
		const allAreFetched = () => {
			for (const dataObject of dataObjects) {
				if (dataObject.isNew) return false
			}
			return true
		}
		return new Promise((resolve, reject) => {
			if (allAreFetched()) {
				resolve(...dataObjects)
				return
			}
			for (const dataObject of dataObjects) {
				dataObject
					.fetch()
					.then(() => {
						if (allAreFetched()) resolve(...dataObjects)
					})
					.catch((err) => {
						if (allAreFetched()) resolve(...dataObjects)
					})
			}
		})
	}
	save() {
		// Tell the server to create (POST) or update (PUT) this model or collection
		return new Promise(
			function (resolve, reject) {
				this.trigger('saving', this)
				const options = Object.assign({}, this.fetchOptions)
				if (this.isNew) {
					options.method = 'post'
				} else {
					options.method = 'put'
				}
				options.body = JSON.stringify(this.data)
				this._innerFetch(this.url, options)
					.then((response) => {
						if (response.status != 200) {
							throw 'Save failed with status ' + response.status
						}
						return response.json()
					})
					.then((data) => {
						data = this.parse(data)
						this.reset(data)
						this._new = false
						this.trigger('saved', this, data, null)
						resolve(this)
					})
					.catch((err) => {
						this.trigger('saved', this, null, err)
						reject(err)
					})
			}.bind(this)
		)
	}
	delete() {
		return new Promise(
			function (resolve, reject) {
				this.trigger('deleting', this)
				const options = Object.assign({}, this.fetchOptions)
				options.method = 'delete'
				this._innerFetch(this.url, options)
					.then((response) => {
						if (response.status != 200) {
							throw 'Delete failed with status ' + response.status
						}
						this.trigger('deleted', this, null)
						resolve()
					})
					.catch((err) => {
						this.trigger('deleted', this, err)
						reject(err)
					})
			}.bind(this)
		)
	}
}

DataObject._NO_CHANGE = Symbol('no change')

export default DataObject

/**
EventListener holds information about listeners on an object with the EventHandler
*/
const EventListener = class {
	constructor(eventName, callback, once = false) {
		this.eventName = eventName
		this.callback = callback
		this.once = once
	}
	matches(eventName) {
		return this.eventName === EventHandler.ALL_EVENTS || eventName === this.eventName
	}
	distributeEvent(eventName, ...params) {
		if (this.matches(eventName)) {
			this.callback(eventName, ...params)
			return true
		}
		return false
	}
}

/**
EventHandler is the base class that implements event distribution
*/
const EventHandler = class {
	/** Send an event to listeners */
	trigger(eventName, ...params) {
		const listenersToRemove = []
		for (const listener of this.listeners) {
			if (listener.distributeEvent(eventName, ...params) && listener.once) {
				listenersToRemove.push(listener)
			}
		}
		for (const listener of listenersToRemove) {
			this.removeListener(listener.callback, listener.eventName)
		}
	}

	/**
	@param {Object|Symbol} [eventName] a string or Symbol indicating the event to watch
	@param {function(eventName: string, eventSource: EventHandler)} callback often includes more parameters that are specific to the event
	@param {boolean} [once=false] If true then the listener is removed after receiving one event
	*/
	addListener(eventName, callback, once = false) {
		this.listeners.push(new EventListener(eventName, callback, once))
	}

	removeListener(eventName, callback) {
		let remove = false
		for (let i = 0; i < this.listeners.length; i++) {
			remove = false
			if (this.listeners[i].callback === callback) {
				if (eventName == EventHandler.ALL_EVENTS) {
					remove = true
				} else if (this.listeners[i].matches(eventName)) {
					remove = true
				}
			}
			if (remove) {
				this.listeners.splice(i, 1)
				i -= 1
			}
		}
	}

	/** @return {EventListener[]} */
	get listeners() {
		if (typeof this._listeners == 'undefined') {
			this._listeners = []
		}
		return this._listeners
	}

	cleanup() {
		if (typeof this._listeners !== 'undefined') {
			this._listeners.length = 0
		}
	}
}
EventHandler.ALL_EVENTS = Symbol('all events')

export default EventHandler

import EventHandler from './EventHandler.js'
import dom from './DOM.js'

let Singleton = null
let MonthNames = null // [locale, [names]]
let DateFieldOrder = null

const TestDateMilliseconds = 1517385600000

const GatheringCookieName = 'potassiumes-localizer-gathering'
/**
Localizer provides the functionality necessary to:

- pick a string translation based on language
- format dates based on locale and time zone

@todo detect language, locale, and timezone
@todo load translations
*/
const Localizer = class extends EventHandler {
	constructor() {
		super()
		/** @type {Map<{string},{Translation}>} */
		this._translations = new Map()
		this._defaultLocales = navigator.languages ? navigator.languages : [navigator.language]
		this._defaultTimeZone = Intl.DateTimeFormat().resolvedOptions().timeZone

		this._dateTimeFormatter = new Intl.DateTimeFormat(this._defaultLocales)

		this._gathering = dom.getCookie(GatheringCookieName) === 'true'
		this._gatheredStrings = this._gathering ? [] : null
	}

	get defaultLocales() {
		return this._defaultLocales
	}

	get defaultTimeZone() {
		return this._defaultTimeZone
	}

	/**
	This should NOT be turned on in production because it continuously grows in memory.
	@return {boolean} true if the Localizer is accumulating strings to translate
	*/
	get gathering() {
		return this._gathering
	}

	set gathering(val) {
		if (!!val) {
			if (this._gathering) return
			dom.setCookie(GatheringCookieName, 'true')
			this._gathering = true
			this._gatheredStrings = []
		} else {
			if (this._gathering === false) return
			dom.removeCookie(GatheringCookieName)
			this._gathering = false
			this._gatheredStrings = null
		}
	}

	get gatheredData() {
		if (this._gathering === false) return null
		return {
			strings: this._gatheredStrings,
		}
	}

	_gatherString(key, value, defaultValue) {
		if (!key || !key.trim()) return
		this._gatheredStrings.push({
			key: key,
			value: value,
			defaultValue: defaultValue,
		})
	}

	translate(key, defaultValue = null) {
		const translation = this._translations.get(key)
		if (!translation) {
			if (this._gathering) this._gatherString(key, null, defaultValue)
			return defaultValue !== null ? defaultValue : key
		}
		const value = translation.get(key)
		if (!value) {
			if (this._gathering) this._gatherString(key, null, defaultValue)
			return defaultValue !== null ? defaultValue : key
		}
		if (this._gathering) this._gatherString(key, value, defaultValue)
		return value
	}

	get monthNames() {
		if (MonthNames === null || MonthNames[0] !== this._defaultLocales[0]) {
			MonthNames = []
			MonthNames[0] = this._defaultLocales
			MonthNames[1] = []
			const options = {
				month: 'long',
			}
			const date = new Date()
			date.setDate(1)
			for (let i = 0; i < 12; i++) {
				date.setMonth(i)
				MonthNames[1].push(date.toLocaleString(this._defaultLocales, options))
			}
		}
		return MonthNames[1]
	}

	/**
	Different locales order their dates in various ways: mm/dd/yyyy or yyyy.mm.dd or 2012 12 20 
	@return {string[]} - a length 3 array of 'day', 'month', and 'year' in the order that this locale renders date fields
	*/
	get dateFieldOrder() {
		if (DateFieldOrder !== null) return DateFieldOrder

		if (typeof this._dateTimeFormatter.formatToParts === 'function') {
			DateFieldOrder = this._dateTimeFormatter
				.formatToParts(new Date(), {
					year: 'numeric',
					month: 'numeric',
					day: 'numeric',
				})
				.filter((part) => part.type !== 'literal')
				.map((part) => part.type)
			return DateFieldOrder
		}

		// Ok the correct but less supported function is not there, try this hack
		const tokens = new Date(TestDateMilliseconds)
			.toLocaleDateString(this._defaultLocales, {
				day: 'numeric',
				month: 'numeric',
				year: 'numeric',
			})
			.split(/[\/ \.]/)
			.filter((token) => token.trim().length > 0)
		let monthIndex = 0
		let yearIndex = 0
		for (let i = 1; i < 3; i++) {
			if (tokens[i].length < tokens[monthIndex].length) monthIndex = i
			if (tokens[i].length > tokens[yearIndex].length) yearIndex = i
		}
		DateFieldOrder = []
		DateFieldOrder[monthIndex] = 'month'
		DateFieldOrder[yearIndex] = 'year'
		for (let i = 0; i < 3; i++) {
			if (!DateFieldOrder[i]) {
				DateFieldOrder[i] = 'day'
				break
			}
		}
		return DateFieldOrder
	}

	formatDateObject(date, options) {
		return date.toLocaleDateString(this._defaultLocales, options)
	}

	formatDate(date, long = false, options = null) {
		return this.formatDateObject(
			date,
			options || {
				year: 'numeric',
				month: long ? 'long' : 'numeric',
				day: 'numeric',
			}
		)
	}

	formatDateTime(date, long = false, options = null) {
		return this.formatDateObject(
			date,
			options || {
				hour: 'numeric',
				minute: 'numeric',
				second: 'numeric',
				hour12: false,
				timeZone: this._defaultTimeZone,
				year: 'numeric',
				month: long ? 'long' : 'numeric',
				day: 'numeric',
			}
		)
	}

	static get Singleton() {
		if (Singleton === null) {
			Singleton = new Localizer()
		}
		return Singleton
	}
}

