import dom from '../../DOM.js'
import Component from '../../Component.js'

/**
SwitchComponent gives the user the ability to flip a switch.
The switch may be on/off or momentary with push-to-break or push-to-make options.

@example <caption>Allow the user to change a boolean data field</caption>
const switchComponent = new SwitchComponent(myDataModel, {
	dataField: 'activated'
})

@todo Add a Bink event on switch change (DataModel 'changed:fieldName' events work, of course)
*/
const SwitchComponent = class extends Component {
	/**
	@param {DataObject} [dataObject=null]
	@param {Object} [options=null]
	@param {string} [options.dataField=null] - a field in dataObject that this switch should reflect and control
	*/
	constructor(dataObject = null, options = {}) {
		super(
			dataObject,
			Object.assign(
				{
					dataField: null,
				},
				options
			)
		)
		this.addClass('switch-component')
		this.setName('SwitchComponent')
		this._on = null

		this._handle = dom
			.div({
				class: 'handle',
			})
			.appendTo(this.dom)

		if (this.options.dataField && this.dataObject) {
			this.dataObject.onFirstReset(() => {
				this._updateFromData()
			})
		} else {
			this.on = true
		}

		this.listenTo('click', this.dom, (ev) => {
			this.on = !this.on
		})
	}

	get on() {
		return this._on
	}

	set on(value) {
		if (this._on === value) return
		this._on = value
		this._updateDisplay()
		if (this.dataObject && this.options.dataField) {
			this.dataObject.set(this.options.dataField, this._on)
		}
	}

	_updateFromData() {
		if (!this.dataObject || !this.options.dataField) return
		this._on = !!this.dataObject.get(this.options.dataField, false)
		this._updateDisplay()
	}

	_updateDisplay() {
		if (this._on) {
			this._handle.innerText = 1
			this.removeClass('off')
			this.addClass('on')
		} else {
			this._handle.innerText = 0
			this.addClass('off')
			this.removeClass('on')
		}
	}
}

export default SwitchComponent
export { SwitchComponent }

import dom from '../../DOM.js'
import Component from '../../Component.js'

/**
TextComponent holds a string which may include paragraphs but not other media.

@example <caption>Show static text</caption>
const textComponent = new TextComponent(undefined, {
	text: 'Static text goes here'
})

@example <caption>Show dynamically bound text</caption>
const textComponent = new TextComponent(myDataModel, {
	dataField: 'title'
})

@example <caption>Show dynamically bound and formatted text</caption>
// Displays either 'Is active' or 'Is not active', depending on the data field
const textComponent = new TextComponent(myDataModel, {
	dataField: 'isActive', // a boolean field
	dataFieldFormatter: (value) => { return value === true ? 'Is active' : 'Is not active' }
})

*/
const TextComponent = class extends Component {
	/**
	@param {Object} [options={}] see the {@link Component} options
	@param {string} [options.text=''] the initial text shown in the heading
	@param {string} [options.dataField=null] a field in the dataObject to bind to as the text
	@param {function} [options.dataFieldFormatter=null] a function that takes in a dataField value and returns a string
	*/
	constructor(dataObject = null, options = {}) {
		super(dataObject, options)
		this.addClass('text-component')
		this.setName('TextComponent')
		this._updateTextFromData = this._updateTextFromData.bind(this)

		this._text = ''

		if (typeof this.options.text === 'string') {
			this.text = this.options.text
		}
		if (this.dataObject && typeof this.options.dataField === 'string') {
			this.listenTo(`changed:${this.options.dataField}`, this.dataObject, this._updateTextFromData)
			this._updateTextFromData()
		}
	}

	/** @type {string} */
	get text() {
		return this._text
	}
	/** @type {string} */
	set text(value) {
		if (this._text === value) return
		this._text = value || ''
		this.dom.innerText = this._text
	}

	_updateTextFromData() {
		if (!this.dataObject || !this.options.dataField) return
		if (this.options.dataFieldFormatter) {
			this.text = this.options.dataFieldFormatter(this.dataObject.get(this.options.dataField) || '')
		} else {
			this.text = this.dataObject.get(this.options.dataField) || ''
		}
	}
}

export default TextComponent
export { TextComponent }

import dom from '../../DOM.js'
import Component from '../../Component.js'

/**
TextInputComponent shows the user a field or area in which to enter text.

@example <caption>Set `TextInputComponent.text` to user's input (no DataModel)</caption>
const component = new TextInputComponent(undefined, {
	text: 'Starting text',
	placeholder: 'Enter some text here' // Shown when no text had been entered
})
// Listen for text changes
component.listenTo(TextInputComponent.TextChangeEvent, (eventName, textValue) => {
	console.log('Text was input', textValue)
})

@example <caption>Bind text input to a DataModel field</caption>
const component = new TextInputComponent(myDataModel, {
	textField: 'biography',
	placeholder: 'Tell us who you are', // Shown when no text had been entered
	submitOnEnter: true
})
// Listen for text submission, usually by pressing the 'Enter' key
component.listenTo(TextInputComponent.TextSubmitEvent, (eventName, newValue) => {
	console.log('New text was submitted', newValue)
})

*/
const TextInputComponent = class extends Component {
	/**
	@param {string} [options.text=''] initial text
	@param {string} [options.dataField=''] a field on the dataObject on which to bind
	@param {string} [options.placeholder=''] text to show when the input is empty
	@param {bool} [options.submitOnEnter=true] trigger a submit event on `enter` keyUp event
	*/
	constructor(dataObject = null, options = {}) {
		super(
			dataObject,
			Object.assign(
				{
					text: '',
					placeholder: '',
					dom: dom.input({ type: 'text' }),
					name: 'TextInputComponent',
					submitOnEnter: true,
				},
				options
			)
		)
		this.addClass('text-input-component')
		this._handleModelChange = this._handleModelChange.bind(this)

		this._placeholderText = this.options.placeholder
		if (this._placeholderText) {
			this.dom.setAttribute('placeholder', this._placeholderText)
		}

		this._text = null
		this._shifted = false

		this.listenTo('input', this.dom, (ev) => {
			this.text = this.dom.value
		})
		this.listenTo('keyup', this.dom, (ev) => {
			if (this.options.submitOnEnter && ev.keyCode === 13) {
				this.trigger(TextInputComponent.TextSubmitEvent, this._text)
				this.text = ''
			}
		})

		if (this.dataObject && this.options.dataField) {
			this.text = this.dataObject.get(this.options.dataField, '')
			this.listenTo(`changed:${this.options.dataField}`, this.dataObject, this._handleModelChange)
		} else {
			this.text = this.options.text
		}
		this.dom.value = this.text
	}

	_handleModelChange() {
		this.text = this.dataObject.get(this.options.dataField, '')
	}

	get text() {
		return this._text
	}
	set text(value) {
		value = value || ''
		if (this._text === value) return
		this._text = value
		const usingPlaceholder = !this._text
		if (usingPlaceholder) {
			this.addClass('placeholder')
		} else {
			this.removeClass('placeholder')
		}
		this.value = this._text
		if (this.dataObject && this.options.dataField && this.dataObject.get(this.options.dataField) !== this._text) {
			this.dataObject.set(this.options.dataField, this._text)
		}
		this.trigger(TextInputComponent.TextChangeEvent, this._text)
	}
}
TextInputComponent.TextChangeEvent = 'text-input-change'
TextInputComponent.TextSubmitEvent = 'text-input-submit'

export default TextInputComponent
export { TextInputComponent }

import dom from '../../DOM.js'
import Component from '../../Component.js'

import ImageComponent from './ImageComponent.js'

/**
ToggleComponent shows a triangle and represents an open or closed state.
The user can change the toggle state by clicking the toggle.

@example
const component = new ToggleComponent()
component.listenTo(ToggleComponent.ToggleEvent, (eventName, toggleComponent, isOpen) => {
	console.log('Toggle is open', isOpen)
})

@todo Allow the use of different emoji or even images
*/
const ToggleComponent = class extends Component {
	/**
	@param {DataObject} [dataObject=null]
	@param {Object} [options=null]
	*/
	constructor(dataObject = null, options = {}) {
		super(
			dataObject,
			Object.assign(
				{
					dom: dom.div('◀'),
					name: 'ToggleComponent',
				},
				options
			)
		)
		this.addClass('toggle-component')
		this._opened = false

		this.listenTo('click', this.dom, (ev) => {
			this.toggle()
		})
	}

	/** @return {boolean} true if the toggle is open */
	get opened() {
		return this._opened
	}

	/**
	Sets the toggle open
	*/
	open() {
		if (this._opened) return
		this._opened = true
		this.addClass('open')
		this.trigger(ToggleComponent.ToggleEvent, this, this._opened)
	}

	/**
	Sets the toggle closed
	*/
	close() {
		if (this._opened === false) return
		this._opened = false
		this.removeClass('open')
		this.trigger(ToggleComponent.ToggleEvent, this, this._opened)
	}

	/**
	@param {boolean} open - whether the toggle should be open or not
	*/
	toggle(open) {
		if (typeof open === 'boolean') {
			if (open) {
				this.close()
			} else {
				this.open()
			}
			return
		}
		if (this._opened) {
			this.close()
		} else {
			this.open()
		}
	}
}

ToggleComponent.ToggleEvent = 'toggle-change-event'

export default ToggleComponent
export { ToggleComponent }

