import ImageComponent from '../atoms/ImageComponent.js'

import CardComponent from './CardComponent.js'

/**
ImageCardComponent is a {@link CardComponent} extension that shows a single image and metadata.
It shows the image along with an optional title and caption from the `CardComponent`.

@example <caption>Use a DataModel to drive the image card.</caption>
const model = new DataModel({
	image: '//static/image.png',
	name: 'Great Image',
	description: 'You will love it'
})

const imageCardComponent = new ImageCardComponent(model, {
	dataField: 'image',
	titleField: 'name',
	captionField: 'description'
})
*/
export default class ImageCardComponent extends CardComponent {
	/**
	@param {DataModel} [dataObject=null]
	@param {Object} [options]
	@param {string} [options.image=null] a URL to an image
	@param {string} [options.dataField=null] the field name in the DataObject that holds the URL to an image
	@param {string} [options.title=null] a string to use as a title
	@param {string} [options.titleField] the field name in the DataObject that holds the title of the image
	@param {string} [options.captionField] the field name in the dataObject that holds the caption
	*/
	constructor(dataObject, options = {}) {
		super(
			dataObject,
			Object.assign(
				{
					image: null,
					dataField: 'image',
					title: null,
					titleField: 'title',
					captionField: 'caption',
				},
				options
			)
		)
		this.addClass('image-card-component')
		this.setName('ImageCardComponent')

		this._imageComponent = new ImageComponent(dataObject, {
			image: this.options.image,
			dataField: this.options.dataField,
		}).appendTo(this.mainComponent)
	}
}

export { ImageCardComponent }

import dom from '../../DOM.js'
import Component from '../../Component.js'

import ToggleComponent from '../atoms/ToggleComponent.js'

/**
MenuComponent holds a set of menu item {@link Component}s and a {@link ToggleComponent} to show and hide the menu.

This class is often used as a base class and in the extending constructor it loads its menu items but it's also possible to use it directly (see below).

@example <caption>Directly create a three item menu</caption>
const menuComponent = new MenuComponent()
menuComponent.appendMenuItem(new MyMenuItemComponent(...))
menuComponent.appendMenuItem(new MyMenuItemComponent(...))
menuComponent.appendMenuItem(new MyMenuItemComponent(...))

menuComponent.addListener(MenuComponent.SelectedEvent, (eventName, menuComponent, selectedIndex) => {
	const menuItemComponent = menuComponent.menuItems[selectedIndex]
})

menuComponent.addListener(ToggleComponent.ToggleEvent, (eventName, menuComponent, isOpen) => {
	// react to the change here
})

*/
const MenuComponent = class extends Component {
	/**
	@param {DataModel} [dataObject=null]
	@param {Object} [options=null]
	*/
	constructor(dataObject = null, options = {}) {
		super(dataObject, options)
		this.addClass('menu-component')
		this.setName('MenuComponent')

		this._selectedIndex = -1
		this._menuItems = []

		this._toggleComponent = new ToggleComponent().appendTo(this)
		this.listenTo(ToggleComponent.ToggleEvent, this._toggleComponent, (eventName, toggleComponent, opened) => {
			if (opened) {
				this.addClass('open')
			} else {
				this.removeClass('open')
			}
			this.trigger(ToggleComponent.ToggleEvent, this, this._toggleComponent.opened)
		})

		/* terrible hack to prevent selection of the toggle, but the user-select CSS is non-standard and needs browser prefixes 😢 */
		this.dom.setAttribute('onselectstart', 'return false;')

		this._menuItemsComponent = new Component()
			.appendTo(this)
			.addClass('menu-items-component')
			.setName('MenuItemsComponent')
	}

	/**
	@type {ToggleComponent}
	*/
	get toggleComponent() {
		return this._toggleComponent
	}

	/**
	@type {Component[]}
	*/
	get menuItems() {
		return this._menuItems
	}

	/**
	@type {number}
	*/
	get selectedIndex() {
		return this._selectedIndex
	}

	/**
	@param {number} index the index of the menu item to set selected
	*/
	set selectedIndex(index) {
		if (!this._menuItems[index]) {
			console.error('no such menu index', index)
			return
		}
		if (this._selectedIndex === index) return
		this._selectedIndex = index
		this._updateSelectionDisplay()
		this.trigger(MenuComponent.SelectedEvent, this, this._selectedIndex)
	}

	/**
	@type {boolean} true if the toggle component is open
	*/
	get opened() {
		return this._toggleComponent.opened
	}

	/**
	Open the toggle component
	*/
	open() {
		this._toggleComponent.open()
	}

	/**
	Close the toggle component
	*/
	close() {
		this._toggleComponent.close()
	}

	/**
	Toggle the toggle component open or closed

	@param {boolean} open
	*/
	toggle(open) {
		this._toggleComponent.toggle(open)
	}

	/**
	@param {Component} component - A menu item Component to add to the menu
	*/
	appendMenuItem(component) {
		this._menuItems.push(component)
		this._menuItemsComponent.append(component)
		component.addClass('menu-item')
		if (this._menuItems.length === 1) {
			this.selectedIndex = 0
		}
	}

	/**
	@param {number} index - the index of the menu item to toggle
	@param {boolean} visible - whether to show or hide the menu item
	*/
	toggleMenuItemVisibility(index, visible) {
		if (index < 0 || index >= this._menuItems.length) return
		if (visible) {
			this._menuItems[index].show()
		} else {
			this._menuItems[index].hide()
		}
	}

	_updateSelectionDisplay() {
		for (let i = 0; i < this._menuItems.length; i++) {
			if (i === this._selectedIndex) {
				this._menuItems[i].addClass('selected')
			} else {
				this._menuItems[i].removeClass('selected')
			}
		}
	}
}

MenuComponent.NavigatedEvent = 'menu-navigated'

export default MenuComponent
export { MenuComponent }

import dom from '../../DOM.js'
import Component from '../../Component.js'
import { lt, ld, ldt } from '../../Localizer.js'

import TextComponent from '../atoms/TextComponent.js'
import LabelComponent from '../atoms/LabelComponent.js'

/**
PaginationComponent shows controls for moving forward and back in list or collection.const

@todo Make this watch a DataCollection or other DataObject that has a pagination interface and events

@example
const paginationComponent = new PaginationComponent(null, {
	totalCount: 10,
	currentIndex: 1
})

*/
const PaginationComponent = class extends Component {
	/**
	@param {DataObject} [dataObject=null]
	@param {Object} [options=null]
	@param {number} [options.totalCount=1]
	@param {number} [options.currentIndex=1]
	*/
	constructor(dataObject = null, options = {}) {
		super(
			dataObject,
			Object.assign(
				{
					totalCount: 1,
					currentIndex: 1,
				},
				options
			)
		)
		this.addClass('pagination-component')
		this.setName('PaginationComponent')

		this._totalCount = this.options.totalCount
		this._currentIndex = this.options.currentIndex

		this._leftArrow = new TextComponent(undefined, { text: lt('Left') })
			.appendTo(this)
			.addClass('left-arrow')
			.setName('LeftArrow')
		this.listenTo('click', this._leftArrow.dom, (ev) => {
			this.currentIndex = this.currentIndex - 1
		})

		this._statusLabel = new LabelComponent().appendTo(this)

		this._rightArrow = new TextComponent(undefined, { text: lt('Right') })
			.appendTo(this)
			.addClass('right-arrow')
			.setName('RightArrow')
		this.listenTo('click', this._rightArrow.dom, (ev) => {
			this.currentIndex = this.currentIndex + 1
		})
		this._updateDisplay()
	}

	/** @type {number} */
	get totalCount() {
		return this._totalCount
	}
	/** @param {number} val */
	set totalCount(val) {
		if (val === this._totalCount) return
		this._totalCount = val
		this._updateDisplay()
	}

	/** @type {number} */
	get currentIndex() {
		return this._currentIndex
	}
	/** @param {number} val */
	set currentIndex(val) {
		if (val === this._currentIndex) return
		if (val < 1 || val > this._totalCount) return
		this._currentIndex = val
		this._updateDisplay()
		this.trigger(PaginationComponent.CurrentIndexChanged, this._currentIndex, this._totalCount, this)
	}

	_updateDisplay() {
		this._statusLabel.text = this._currentIndex + '\n' + lt('of') + '\n' + this._totalCount
		if (this._currentIndex === 1) {
			this._leftArrow.addClass('disabled')
		} else {
			this._leftArrow.removeClass('disabled')
		}
		if (this._currentIndex === this._totalCount) {
			this._rightArrow.addClass('disabled')
		} else {
			this._rightArrow.removeClass('disabled')
		}
	}
}

PaginationComponent.CurrentIndexChanged = Symbol('current-index-changed')

export default PaginationComponent
export { PaginationComponent }

