import dom from '../../DOM.js'
import { lt, ld, ldt } from '../../Localizer.js'

import Component from '../../Component.js'

import VideoComponent from '../atoms/VideoComponent.js'
import ButtonComponent from '../atoms/ButtonComponent.js'
import SliderComponent from '../atoms/SliderComponent.js'

/**
VideoPlayerComponent shows a {@link VideoComponent} with play/pause and shuttle controls.

*/
const VideoPlayerComponent = class extends Component {
	/**
	@param {DataObject} [dataObject=null]
	@param {Object} [options=null]
	@param {string} [options.video=''] - a URL to a video
	@param {string} [options.mimeType=''] - the MIME type for the video, like 'video/mp4'
	*/
	constructor(dataObject = null, options = {}) {
		super(
			dataObject,
			Object.assign(
				{
					video: '',
					mimeType: '',
				},
				options
			)
		)
		this.addClass('video-player-component')
		this.setName('VideoPlayerComponent')
		this._updateDisplay = this._updateDisplay.bind(this)

		this._backdropComponent = new Component().appendTo(this)
		this._backdropComponent.addClass('backdrop-component')
		this._backdropComponent.setName('BackdropComponent')

		this._videoComponent = new VideoComponent(undefined, {
			video: this.options.video,
			mimeType: this.options.mimeType,
		}).appendTo(this._backdropComponent)
		this.listenTo(VideoComponent.VideoInitializedEvent, this._videoComponent, (eventName, component) => {
			const video = this._videoComponent.video
			this.listenTo('playing', video, this._updateDisplay)
			this.listenTo('play', video, this._updateDisplay)
			this.listenTo('timeupdate', video, this._updateDisplay)
			this.listenTo('pause', video, this._updateDisplay)
			this.listenTo('ended', video, this._updateDisplay)
		})

		this._controlsComponent = new Component(null, {}).appendTo(this)
		this._controlsComponent.addClass('video-player-controls')
		this._controlsComponent.setName('VideoPlayerControls')

		this._toggleButtonComponent = new ButtonComponent()
			.appendTo(this._controlsComponent)
			.addClass('toggle-button-component')
			.setName('ToggleButtonComponent')
		this.listenTo(ButtonComponent.ActivatedEvent, this._toggleButtonComponent, (eventName) => {
			this._videoComponent.toggle()
		})

		this._sliderComponent = new SliderComponent().appendTo(this._controlsComponent)
		this.listenTo(SliderComponent.ValueChangeViaInputEvent, this._sliderComponent, (eventName, newFraction) => {
			this._videoComponent.currentTime = newFraction * Math.max(1, this._videoComponent.duration)
		})
		this._updateDisplay()
	}

	/** @type {HTMLElement} */
	get video() {
		return this._videoComponent.video
	}

	/**
	@param {string} url - the relative or full URL to the video
	@param {string} mimeType - a mime type like 'video/mp4'
	*/
	setVideoSource(url, mimeType) {
		this._videoComponent.setSourceAttributes(url, mimeType)
	}

	_updateDisplay() {
		if (this._sliderComponent.userIsChanging === false) {
			if (this._videoComponent.currentTime === 0) {
				this._sliderComponent.valueFraction = 0
			} else {
				this._sliderComponent.valueFraction =
					Math.max(0, this._videoComponent.currentTime) /
					Math.max(1, this._videoComponent.duration, this._videoComponent.currentTime)
			}
		}
		if (this._videoComponent.paused) {
			this._toggleButtonComponent.text = lt('Play')
		} else {
			this._toggleButtonComponent.text = lt('Pause')
		}
	}
}

export default VideoPlayerComponent
export { VideoPlayerComponent }

import dom from '../../DOM.js'
import { lt, ld, ldt } from '../../Localizer.js'

import LabelComponent from '../atoms/LabelComponent.js'

/**
WaitComponent shows a spinner to reassure the user that a process is happening.

@todo Make this watch a DataObject and react to events like saving or fetching and update

@example
const waitComponent = new WaitComponent()
// at start of some process
waitComponent.state = WaitComponent.PROCESSING
// at successful end of some process
waitComponent.state = WaitComponent.SUCCEEDED
// or at successful end of some process
waitComponent.state = WaitComponent.FAILED
// or to reset to nothing happening
waitComponent.state = WaitComponent.NOTHING_HAPPENING

*/
const WaitComponent = class extends LabelComponent {
	/**
	@param {DataObject} [dataObject=null]
	@param {Object} [options=null]
	*/
	constructor(dataObject = null, options = {}) {
		super(dataObject, options)
		this.addClass('wait-component')
		this.setName('WaitComponent')

		this._state = WaitComponent.NOTHING_HAPPENING
		this._animationInterval = null
		this._animationIndex = 0

		this._updateDisplay()
	}

	/**
	Will be one of:

	- WaitComponent.NOTHING_HAPPENING
	- WaitComponent.PROCESSING
	- WaitComponent.FAILED
	- WaitComponent.SUCCEEDED

	@type {string}
	*/
	get state() {
		return this._state
	}

	/**
	Must be one of:

	- WaitComponent.NOTHING_HAPPENING
	- WaitComponent.PROCESSING
	- WaitComponent.FAILED
	- WaitComponent.SUCCEEDED

	@param {string} val
	*/
	set state(val) {
		if (this._state === val) return
		if (_states.includes(val) === false) {
			console.error('unknown state', val)
			return
		}
		this._state = val
		this._updateDisplay()
	}

	_startAnimation() {
		if (this._animationInterval !== null) return
		this._animationIndex = 0
		this._animationInterval = setInterval(() => {
			this.removeClass(..._animationClassNames)
			this.addClass(_animationClassNames[this._animationIndex])
			this._animationIndex = (this._animationIndex + 1) % _animationClassNames.length
		}, _animationTicks)
	}

	_stopAnimation() {
		if (this._animationInterval !== null) {
			clearInterval(this._animationInterval)
			this._animationInterval = null
		}
		this._animationIndex = 0
		this.removeClass(..._animationClassNames)
	}

	_updateDisplay() {
		switch (this._state) {
			case WaitComponent.NOTHING_HAPPENING:
				this._stopAnimation()
				this.removeClass('processing', 'failed', 'succeeded')
				this.addClass('nothing-happening')
				this.text = lt('')
				break
			case WaitComponent.PROCESSING:
				this._startAnimation()
				this.removeClass('nothing-happening', 'failed', 'succeeded')
				this.addClass('processing')
				this.text = lt('Processing...')
				break
			case WaitComponent.FAILED:
				this._stopAnimation()
				this.removeClass('nothing-happening', 'processing', 'succeeded')
				this.addClass('failed')
				this.text = lt('Failed')
				break
			case WaitComponent.SUCCEEDED:
				this._stopAnimation()
				this.removeClass('nothing-happening', 'processing', 'failed')
				this.addClass('succeeded')
				this.text = lt('Succeeded')
				break
		}
	}
}

WaitComponent.NOTHING_HAPPENING = 'nothing-happening'
WaitComponent.PROCESSING = 'processing'
WaitComponent.FAILED = 'failed'
WaitComponent.SUCCEEDED = 'succeeded'

const _animationTicks = 1500

const _animationClassNames = ['wait-top', 'wait-right', 'wait-bottom', 'wait-left']

const _states = [
	WaitComponent.NOTHING_HAPPENING,
	WaitComponent.PROCESSING,
	WaitComponent.FAILED,
	WaitComponent.SUCCEEDED,
]

export default WaitComponent
export { WaitComponent }

